import { useState } from "react";
import Plx from "react-plx";
import { ParallaxProvider, useParallax } from "react-scroll-parallax";
import "./App.scss";
import logo from "./images/logo1.jpeg";
import logo1 from "./images/logo2.jpeg";
import menu from "./images/menu.png";

function App() {
  const [showDropdown, setShowDropdown] = useState(false);
  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  };

  const parallax = useParallax({
    rotate: [0, 360],
  });

  const parallaxData = [
    {
      start: 0,
      end: 400,
      properties: [
        {
          startValue: 1,
          endValue: 0,
          property: "scale",
        },
      ],
    },
  ];

  const data = useParallax({
    scale: [0.5, 1, "easeInQuad"],
  });

  return (
    <ParallaxProvider>
    <div className="App">
      <nav className="nav">
        <p style={{ textAlign: "center" }}>NextJs/ReactJs Assignment</p>
        <div className="nav-button">
          <button className="nav-hover" onClick={toggleDropdown}>
            <img src={menu} alt="..." className="menu" />
          </button>
          {showDropdown && (
            <div className="dropdown">
              <a href="#">Login</a>
            </div>
          )}
        </div>
      </nav>
      <Plx parallaxData={parallaxData}>
        <div className="logo">
          <img
            ref={data.ref}
            src={logo}
            alt=".."
            style={{ width: "400px", padding: "5px" }}
          />
          <img ref={data.ref} src={logo1} alt=".." style={{ width: "400px" }} />
        </div>
      </Plx>
    </div>
    </ParallaxProvider>
  );
}

export default App;
